import React, { useState } from "react";
import { createWorker } from "tesseract.js";
import "./App.css";

function App() {
  const [file, setFile] = useState(null);
  const [ocr, setOcr] = useState("No File Selected");
  const [lang,setLang] = useState("eng");


  console.log(lang);

  const worker = createWorker({
    logger: (m) => console.log(m),
  });

  const doOCR = async () => {
    await worker.load();
    await worker.loadLanguage(lang);
    await worker.initialize(lang);
    const {
      data: { text },
    } = await worker.recognize(file);
    setOcr(text);
  };
  const handleOnClick = (e) => {
    // e.preventDefault();
    doOCR();
  };

  return (
    <div className="container">
      <div className="heading center-align">
        <h1>OCR APP</h1>
      </div>
      <div className="row">
        <div className="col s6">
          <div className="box">
            <form action="/">
              <input
                className="fileupload"
                type="file"
                accept=".jpg, .jpeg, .png"
                onChange={(e) => setFile(e.target.files[0])}
              />
              
              <a className="button btn-floating btn-large waves-effect waves-light red" onClick={handleOnClick} ><i className="material-icons">done</i></a>
              
              <div className="checkBoxes ">
                <p className='radio center-align'>
                  <div className="row">
                    <div className="col s4">
                      <label>
                      <input 

                      className="with-gap"                       
                      name="group1"                       
                      type="radio"
                      value="hin"
                      onChange={(e) => setLang(e.target.value)}
                      />
                      
                      <span>Hindi</span>
                      </label>                      
                    </div>
                    <div className="col s4">
                      <label>
                      <input 
                      className="with-gap" 
                      name="group1" 
                      type="radio" 
                      value="eng"
                      defaultChecked
                      onChange={(e) => setLang(e.target.value)}

                      />

                      <span>English</span>
                      </label>

                    </div>
                    <div className="col s4">
                      <label>
                      <input className="with-gap" name="group1" type="radio" />
                      <span>Marathi</span>
                     </label>
                    </div>
                  </div>                 
                </p>
              </div>
            </form>
          </div>
        </div>
        <div className="col s6">
          <div className="box">
            
            <div className="output">{ocr}</div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
