import React, { useState } from "react";
import { createWorker, recognize, setLogging } from "tesseract.js";
import "./App.css";
import { jsPDF } from "jspdf";
import myFont from "./KRDEV011.ttf";

function App() {
  const [file, setFile] = useState(null);
  const [ocr, setOcr] = useState("No File Selected");
  const [lang, setLang] = useState("eng");

  const worker = createWorker({
    logger: (m) => console.log(m),

  });
 
  const doOCR = async () => {
    await worker.load();
    await worker.loadLanguage(lang);
    await worker.initialize(lang);  
    const {data: { text },} = await worker.recognize(file);           
    setOcr(text);
    worker.getPDF("Result",true);
  };
  const handleOnClick = (e) => {
    e.preventDefault();
    doOCR();
    
  };
  setLogging(true);
 
  const handleDownload = () => {

    const doc = new jsPDF();     
      
      doc.text(ocr, 10, 10);
      doc.save("OCR-APP_Result.pdf");

  }
  
  return (
    <div className="container">
      <div className="heading center-align">
        <h1>OCR APP</h1>
      </div>
      <div className="row">
        <div className="col s6">
          <div className="box">
            <form action="/">
              <input
                className="fileupload"
                type="file"
                accept=".jpg, .jpeg, .png"
                onChange={(e) => setFile(e.target.files[0])}
              />
              <div className="preview">
                <img src={file} />
              </div>
              
              <a
                href=""
                className="button btn-floating btn-large waves-effect waves-light red"
                onClick={handleOnClick}
              >
                <i className="material-icons">done</i>
              </a>

              <div className="checkBoxes ">
                <div className="radio center-align">
                  <div className="row">
                    <div className="col s4">
                      <label>
                        <input
                          className="with-gap"
                          name="group1"
                          type="radio"
                          value="hin"
                          onChange={(e) => setLang(e.target.value)}
                        />

                        <span>Hindi</span>
                      </label>
                    </div>
                    <div className="col s4">
                      <label>
                        <input
                          className="with-gap"
                          name="group1"
                          type="radio"
                          value="eng"
                          defaultChecked
                          onChange={(e) => setLang(e.target.value)}
                        />

                        <span>English</span>
                      </label>
                    </div>
                    <div className="col s4">
                      <label>
                        <input
                          className="with-gap"
                          name="group1"
                          type="radio"
                          value="mar"
                          onChange={(e) => setLang(e.target.value)}
                        />
                        <span>Marathi</span>
                      </label>
                    </div>
                  </div>
                </div>
              </div>
            </form>
          </div>
        </div>
        <div className="col s6">
          <div className="box">
            <div className="output">{ocr}</div>
            <a class="download btn-floating btn-medim waves-effect waves-light red " onClick={handleDownload}><i class="material-icons">file_download</i></a>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
