import React, { useState } from "react";
import { createWorker } from "tesseract.js";
import "./App.css";

function App() {
  const [file, setFile] = useState(null);
  const [ocr, setOcr] = useState("No File Selected");

  const worker = createWorker({
    logger: (m) => console.log(m),
  });

  /************************************/
  const doOCR = async () => {
    await worker.load();
    await worker.loadLanguage("eng");
    await worker.initialize("eng");
    const {
      data: { text },
    } = await worker.recognize(file);
    setOcr(text);
  };
  /*************************************/
  const handleOnClick = () => {
    doOCR();
  };

  return (
    <div className="container">
      <div className="heading center-align">
        <h1>OCR APP</h1>
      </div>
      <div class="row">
        <div class="col s6">
          <div className="box">
            <form action="">
              <input
                className="fileupload"
                type="file"
                accept=".jpg, .jpeg, .png"
                onChange={(e) => setFile(e.target.files[0])}
              />
              <button onClick={handleOnClick}>Submit</button>
              <div className="checkBoxes center-align ">
                <p className='radio  '>
                  <label>
                    <input class="with-gap" name="group1" type="radio" />
                    <span>Green</span>
                  </label>
                  <label>
                    <input class="with-gap" name="group1" type="radio" />
                    <span>Green</span>
                  </label>
                  <label>
                    <input class="with-gap" name="group1" type="radio" />
                    <span>Green</span>
                  </label>
                </p>
              </div>
            </form>
          </div>
        </div>
        <div class="col s6">
          <div className="box">
            <div></div>
            <div className="output">{ocr}</div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
